```java
    public V put(K key, V value) {  
           //t  
            Entry<K,V> t = root;  
            //tnullTreeMap  
            if (t == null) {  
                //key  
                compare(key, key); // type (and possibly null) check  
                //key-valueEntryroot  
                root = new Entry<>(key, value, null);  
                //size = 1TreeMap  
                size = 1;  
                // + 1  
                modCount++;  
                return null;  
            }  
            int cmp;     //cmpkey  
            Entry<K,V> parent;   //  
            // split comparator and comparable paths  
            Comparator<? super K> cpr = comparator;    //  
            //cprTreeMap  
            if (cpr != null) {  
                do {  
                    parent = t;      //parentt  
                    //keykey  
                    cmp = cpr.compare(key, t.key);  
                    //cmp0keykey  
                    if (cmp < 0)  
                        t = t.left;  
                    //cmp0keykey  
                    else if (cmp > 0)  
                        t = t.right;  
                    //cmp0key  
                    else  
                        return t.setValue(value);  
                } while (t != null);  
            }  
            //cprTreeMap  
            else {  
                if (key == null)     //key  
                    throw new NullPointerException();  
                /*  */  
                Comparable<? super K> k = (Comparable<? super K>) key;  
                do {  
                    parent = t;  
                    cmp = k.compareTo(t.key);  
                    if (cmp < 0)  
                        t = t.left;  
                    else if (cmp > 0)  
                        t = t.right;  
                    else  
                        return t.setValue(value);  
                } while (t != null);  
            }  
            //parent  
            Entry<K,V> e = new Entry<>(key, value, parent);  
            //keyparentkey  
            if (cmp < 0)  
                parent.left = e;  
          //keyparentkey  
            else  
                parent.right = e;  
            /*  
             *    
             *  fixAfterInsertion()  
             */  
            fixAfterInsertion(e);  
            //TreeMap + 1  
            size++;  
            //TreeMap + 1  
            modCount++;  
            return null;  
        }  
