    private void deleteEntry(Entry<K,V> p) {  
        modCount++;      // +1  
        size--;          // -1  

        /* 
         *  p p  
         * successor(P)P  
         * ---------------------1 
         */  
        if (p.left != null && p.right != null) {    
            Entry<K,V> s = successor(p);  
            p.key = s.key;  
            p.value = s.value;  
            p = s;  
        }  

        //replacementP  
        Entry<K,V> replacement = (p.left != null ? p.left : p.right);  

        /* 
         *  
         * -----------------------2 
         */  
        //  
        if (replacement != null) {  
            replacement.parent = p.parent;  
            /* 
             *replacementP 
             */  
            //Preplacement  
            if (p.parent == null)  
                root = replacement;  
            //Preplacement  
            else if (p == p.parent.left)  
                p.parent.left  = replacement;  
          //Preplacement  
            else  
                p.parent.right = replacement;  

            //P  
            p.left = p.right = p.parent = null;  

            /* 
             * P 
             * P 
             */  
            if (p.color == BLACK)  
                fixAfterDeletion(replacement);  
        } else if (p.parent == null) {     //pP  
            root = null;  
        } else {      //P  
            if (p.color == BLACK)         //P  
                fixAfterDeletion(p);  

            //P  
            if (p.parent != null) {  
                if (p == p.parent.left)  
                    p.parent.left = null;  
                else if (p == p.parent.right)  
                    p.parent.right = null;  
                p.parent = null;  
            }  
        }  
    }  
