    private void rotateLeft(Entry<K,V> p) {  
        if (p != null) {  
            //PN  
            Entry<K,V> r = p.right;  
            //RP  
            p.right = r.left;  
            //RPR  
            if (r.left != null)  
                r.left.parent = p;  
            //PR  
            r.parent = p.parent;  
            //PR  
            if (p.parent == null)  
                root = r;  
            //PGRP(G)  
            else if (p.parent.left == p)  
                p.parent.left = r;  
            //RPG  
            else  
                p.parent.right = r;  
            //PR  
            r.left = p;  
            //RP  
            p.parent = r;  
        }  
    }  
