    public synchronized void removeElementAt(int index) {  
        modCount++;     //+1  
        if (index >= elementCount) {   //  
            throw new ArrayIndexOutOfBoundsException(index + " >= " + elementCount);  
        }  
        else if (index < 0) {    // < 0  
            throw new ArrayIndexOutOfBoundsException(index);  
