```java
    public class StringTest {  

        /** 
        * @desc +concat()append()10W 
        * @author chenssy 
        * @data 2013-11-16 
        * @param args 
        * @return void 
        */  
        public static void main(String[] args) {  
            //+  
            long start_01 = System.currentTimeMillis();  
            String a = "a";  
            for(int i = 0 ; i < 100000 ; i++){  
                a += "b";  
            }  
            long end_01 = System.currentTimeMillis();  
            System.out.println("  +   " + (end_01 - start_01) + "");  

            //concat()  
            long start_02 = System.currentTimeMillis();  
            String c = "c";  
            for(int i = 0 ; i < 100000 ; i++){  
                c = c.concat("d");  
            }  
            long end_02 = System.currentTimeMillis();  
            System.out.println("concat" + (end_02 - start_02) + "");  

            //append  
            long start_03 = System.currentTimeMillis();  
            StringBuffer e = new StringBuffer("e");  
            for(int i = 0 ; i < 100000 ; i++){  
                e.append("d");  
            }  
            long end_03 = System.currentTimeMillis();  
            System.out.println("append" + (end_03 - start_03) + "");  
        }  
    }  

    ------------  
